import React, { Component } from 'react';
import './App.css';
import axios from 'axios';

class App extends Component {
  constructor(props) {
    super(props)
    this.state = {
      users: [],
      name: "",
      age: "",
      job: ""
    }
    // this.getAllUsers = this.getAllUsers.bind(this);
    // this.handleNameChange = this.handleNameChange.bind(this);
    this.handleNameChange = this.handleNameChange.bind(this);
    this.handleAgeChange = this.handleAgeChange.bind(this);
    this.handleJobChange = this.handleJobChange.bind(this);
    this.submitUser = this.submitUser.bind(this);
  }

  componentDidMount(){
    axios.get('http://localhost:8080/users/')
    .then((res) => {
      console.log(res.data);
      this.setState({users: res.data})
    })
    .catch((err) => {
      console.log(err);
    })
  }

  // getAllUsers() {
  //   axios.get('http://localhost:8080/users/')
  //   .then((res) => {
  //     this.setState({users: res.data})
  //   })
  //   .catch((err) => {
  //     console.log(err);
  //   })
  // }

  // getSingleUser() {
  //   console.log('clicked')
  //   var url = 'http://localhost:8080/users/' + this.state.userId;
  //   axios.get(url)
  //   .then((res) => {
  //     this.setState({user: res})
  //   })
  //   .catch((err) => {
  //     console.log(err);
  //   })
  // }

  handleNameChange(event) {
    this.setState({name: event.target.value});
  }

  handleAgeChange(event) {
    this.setState({age: event.target.value});
  }

  handleJobChange(event) {
    this.setState({job: event.target.value});
  }

  submitUser(event) {
    event.preventDefault();
    console.log(this.state);
    var user = {
      name: this.state
    }
    axios.post('http://localhost:8080/users/add', user)
    .then((res) => {
      console.log(res.data);
      this.setState({users: res.data})
    })
    .catch((err) => {
      console.log(err);
    })
  }

  render() {
    return (
      <div className="App">
        <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
          <a className="navbar-brand" href="/">AXIOS HTTP CLIENT</a>
          <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
            <span className="navbar-toggler-icon"></span>
          </button>
        </nav>
        <div className="container" style={{marginTop: '50px'}}>
          <div className="card">
            <div className="card-body">
              <h4 className="text-left">GET REQUEST</h4><hr/>
              <div className="input-group mb-3">
                <input type="text" className="form-control" disabled placeholder="http://localhost:8080/users/"/>

              </div>
              <div className="row">
              <table className="table table-bordered table-striped" style={{marginRight: '15px', marginLeft: '15px'}}>
                <thead>
                  <tr>
                    <th>#</th>
                    <th>Name</th>
                    <th>Age</th>
                    <th>Job</th>
                  </tr>
                </thead>
                <tbody>
                  {this.state.users.map((user,i) => {
                    return (                      
                      <tr key={i}>
                        <td>{i+1}</td>
                        <td>{user.name}</td>
                        <td>{user.age}</td>
                        <td>{user.job}</td>
                      </tr>                     
                    )
                  })}
                </tbody>
              </table>  
              </div>              
              <br/>
              <h4 className="text-left">POST REQUEST</h4><hr/>
              <form onSubmit={this.submitUser}>
                <div className="form-row">
                  <div className="col-md-3 mb-3">
                    <input type="text" className="form-control" name="name" onChange={this.handleNameChange} placeholder="Username"/>
                  </div>
                  <div className="col-md-3 mb-3">
                    <input type="text" className="form-control" name="age" onChange={this.handleAgeChange} placeholder="Age"/>
                  </div>
                  <div className="col-md-3 mb-3">
                    <input type="text" className="form-control" name="job" onChange={this.handleJobChange} placeholder="Job"/>
                  </div>
                  <span>
                    <button className="btn btn-primary" style={{marginTop: '0px'}} type="submit">ADD USER</button>
                  </span>                 
                </div>

              </form>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default App;
