import React, { Component } from 'react';
import './App.css';
import axios from 'axios';

class App extends Component {
  constructor(props) {
    super(props)
    this.state = {
      users: []
    }
    // this.getAllUsers = this.getAllUsers.bind(this);
    // this.getSingleUser = this.getSingleUser.bind(this);
  }

  componentDidMount(){
    axios.get('http://localhost:8080/users/')
    .then((res) => {
      console.log(res.data);
      this.setState({users: res.data})
    })
    .catch((err) => {
      console.log(err);
    })
  }

  getAllUsers() {
    axios.get('http://localhost:8080/users/')
    .then((res) => {
      this.setState({users: res.data})
    })
    .catch((err) => {
      console.log(err);
    })
  }

  getSingleUser() {
    console.log('clicked')
    var url = 'http://localhost:8080/users/' + this.state.userId;
    axios.get(url)
    .then((res) => {
      this.setState({user: res})
    })
    .catch((err) => {
      console.log(err);
    })
  }

  handleIdChange(event) {
    this.setState({userId: event.target.value});
  }

  render() {
    return (
      <div className="App">
        <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
          <a className="navbar-brand" href="/">AXIOS HTTP CLIENT</a>
          <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
            <span className="navbar-toggler-icon"></span>
          </button>
        </nav>
        <div className="container" style={{marginTop: '50px'}}>
          <div className="card">
            <div className="card-body">
              <h4 className="text-left">GET REQUEST</h4>
              <div className="input-group mb-3">
                <input type="text" className="form-control" disabled name="userId" onChange={this.handleIdChange} placeholder="http://localhost:8080/users/"/>
                  <button className="btn btn-primary btn-sm">GET ALL</button>
              </div>
              <div className="row">
              <table className="table table-bordered table-striped" style={{marginRight: '15px', marginLeft: '15px'}}>
                <thead>
                  <tr>
                    <th>#</th>
                    <th>Name</th>
                    <th>Age</th>
                    <th>Job</th>
                  </tr>
                </thead>
                <tbody>
                  {this.state.users.map((user,i) => {
                    return (                      
                      <tr key={i}>
                        <td>{i+1}</td>
                        <td>{user.name}</td>
                        <td>{user.age}</td>
                        <td>{user.job}</td>
                      </tr>                     
                    )
                  })}
                </tbody>
              </table>  
              </div>


              <br/>
              <h4 className="text-left">POST REQUEST</h4>
              <form>
                <div class="form-row">
                  <div class="col-md-3 mb-3">
                    <label for="validationDefault01">First name</label>
                    <input type="text" class="form-control" id="validationDefault01" placeholder="First name" value="Mark" required/>
                  </div>
                  <div class="col-md-3 mb-3">
                    <label for="validationDefault02">Last name</label>
                    <input type="text" class="form-control" id="validationDefault02" placeholder="Last name" value="Otto" required/>
                  </div>
                  <div class="col-md-3 mb-3">
                    <label for="validationDefaultUsername">Username</label>
                    <div class="input-group">
                      <div class="input-group-prepend">
                        <span class="input-group-text" id="inputGroupPrepend2">@</span>
                      </div>
                      <input type="text" class="form-control" id="validationDefaultUsername" placeholder="Username" aria-describedby="inputGroupPrepend2" required/>
                    </div>
                  </div>

                </div>
                <button class="btn btn-primary" type="submit">Submit form</button>
              </form>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default App;
