import React, { Component } from 'react';
import './App.css';
import axios from 'axios';

class App extends Component {
  constructor(props) {
    super(props)
    this.state = {
      users: []
    }
    this.getAllUsers = this.getAllUsers.bind(this);
  }

  // componentDidMount(){
  //   axios.get('http://localhost:8080/users/')
  //   .then((res) => {
  //     console.log(res.data);
  //   })
  //   .catch((err) => {
  //     console.log(err);
  //   })
  // }

  getAllUsers() {
    axios.get('http://localhost:8080/users/')
    .then((res) => {
      this.setState({users: res.data})
    })
    .catch((err) => {
      console.log(err);
    })
  }

  getSingleUser() {
    axios.get('http://localhost:8080/users/', {
        params: {
          id: ''
        }
    })
    .then((res) => {
      this.setState({users: res.data})
    })
    .catch((err) => {
      console.log(err);
    })
  }

  render() {
    return (
      <div className="App">
        <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
          <a className="navbar-brand" href="/">AXIOS HTTP CLIENT</a>
          <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
            <span className="navbar-toggler-icon"></span>
          </button>
        </nav>
        <div className="container" style={{marginTop: '50px'}}>
          <div className="card">
            <div className="card-body">
              <h4 className="text-left">GET ALL USERS</h4>
              <div className="input-group mb-3">
                <input type="text" className="form-control" disabled placeholder="http://localhost:8080/users/" aria-label="Recipient's username" aria-describedby="basic-addon2"/>
                  <button className="btn btn-primary btn-sm" onClick={this.getAllUsers}>GET ALL</button>
              </div>
              {this.state.users.map((user,i) => {
                return (
                  <div key={i}>
                      {user.name}
                  </div>
                )
              })}
              <br/>
              <h4 className="text-left">GET A SINGLE USER</h4>
              <div className="input-group mb-3">
                <input type="text" className="form-control" disabled placeholder="http://localhost:8080/users/" aria-label="Recipient's username" aria-describedby="basic-addon2"/>
                  <button className="btn btn-primary btn-sm" onClick={this.getAllUsers}>GET USER</button>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default App;
