import React, { Component } from 'react';
import './App.css';
import axios from 'axios';

class App extends Component {
  constructor(props) {
    super(props)
    this.state = {
      users: []
    }
    // this.getAllUsers = this.getAllUsers.bind(this);
    // this.getSingleUser = this.getSingleUser.bind(this);
  }

  componentDidMount(){
    axios.get('http://localhost:8080/users/')
    .then((res) => {
      console.log(res.data);
      this.setState({users: res.data})
    })
    .catch((err) => {
      console.log(err);
    })
  }

  getAllUsers() {
    axios.get('http://localhost:8080/users/')
    .then((res) => {
      this.setState({users: res.data})
    })
    .catch((err) => {
      console.log(err);
    })
  }

  getSingleUser() {
    console.log('clicked')
    var url = 'http://localhost:8080/users/' + this.state.userId;
    axios.get(url)
    .then((res) => {
      this.setState({user: res})
    })
    .catch((err) => {
      console.log(err);
    })
  }

  handleIdChange(event) {
    this.setState({userId: event.target.value});
  }

  render() {
    return (
      <div className="App">
        <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
          <a className="navbar-brand" href="/">AXIOS HTTP CLIENT</a>
          <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
            <span className="navbar-toggler-icon"></span>
          </button>
        </nav>
        <div className="container" style={{marginTop: '50px'}}>
          <div className="card">
            <div className="card-body">
              <h4 className="text-left">GET REQUEST</h4>
              <div className="input-group mb-3">
                <input type="text" className="form-control" disabled name="userId" onChange={this.handleIdChange} placeholder="http://localhost:8080/users/" aria-label="Recipient's username" aria-describedby="basic-addon2"/>
                  <button className="btn btn-primary btn-sm">GET ALL</button>
              </div>
              <div className="table table-striped">
                <thead>
                  <tr>
                    <th>#</th>
                    <th>Name</th>
                    <th>Age</th>
                    <th>Job</th>
                  </tr>
                </thead>
                <tbody>

                </tbody>
                  </div>
              {this.state.users.map((user,i) => {
                return (


                        <tr key={i}>
                          <td>{i}</td>
                          <td>{user.name}</td>
                          <td>{user.age}</td>
                          <td>{user.job}</td>
                        </tr>

                )
              })}
              <br/>
              <h4 className="text-left">POST REQUEST</h4>

            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default App;
